mapper.select:
select contactNo, case when name='mobile' then (select name from contacts where no = contactNo) when name='' then (select name from contacts where no = contactNo) else name end name, constructionDate from estimate where delyn = 'n'
and constructionDate between #{sDm} and #{eDm}
and contactNo = #{searchKeyword}
and name like CONCAT('%',#{searchKeyword},'%')
group by contactNo,constructionDate order by regDate desc limit #{fst},#{lst}

select count(*) from ( select no from estimate where delyn = 'n'
and constructionDate between #{sDm} and #{eDm}
and contactNo = #{searchKeyword}
and name like CONCAT('%',#{searchKeyword},'%')
group by contactNo,constructionDate )tb
